mapper.select:
SELECT * FROM DEVICE 
WHERE 1=1

AND MEMBERCODE = #{uMCode} AND DEVICESORT = #{deviceSort}


AND DEVICECODE = #{deviceCode}

mapper.update:
UPDATE COMMAND 
SET WORKYN = "Y"
WHERE COMCODE = #{_parameter}

UPDATE SPEAKERMUSIC
SET DEVICECODE = #{speakerCode},
	MUSICCODE = #{musicCode}
WHERE DEVICECODE = #{speakerCode}
